function openTab(e,t){var a,s,l;for(s=document.getElementsByClassName("content-tab"),a=0;a<s.length;a++)s[a].style.display="none";for(l=document.getElementsByClassName("tab"),a=0;a<s.length;a++)l[a].className=l[a].className.replace(" is-active","");document.getElementById(t).style.display="block",e.currentTarget.className+=" is-active"}
